function App() {
  const ctx = useContext(AuthContext);
  // const stroedUserInfo = localStorage.setItem('loggedIn', '1');
  // if(stroedUserInfo === '1')
  // {
  //   setIsLoggedIn(true);
  // }

  return (
    <React.Fragment>
      <MainHeader />
      <main>
        {!ctx.isLoggedIn && <Login />}
        {ctx.isLoggedIn && <Home />}
      </main>
    </React.Fragment>
  );
}

export default App;
